import { mapState, mapActions, mapMutations } from "vuex";
const mapStore = "mapStore";
export default {
  name: "HouseSearchBar",
  data() {
    return {
      sidoCode: null,
      gugunCode: null,
      dongCode: null
    };
  },
  computed: {
    ...mapState(mapStore, ["sidos", "guguns", "dongs", "houses", "officetels"])
  },
  created() {
    this.CLEAR_SIDO_LIST();
    this.getSido();
  },
  methods: {
    ...mapActions(mapStore, ["getSido", "getGugun", "getDong", "getHouseList", "getOfficetelList"]),
    ...mapMutations(mapStore, ["CLEAR_SIDO_LIST", "CLEAR_GUGUN_LIST", "CLEAR_DONG_LIST", "SET_HOUSE_LIST", "SET_OFFICETEL_LIST"]),
    gugunList() {
      // console.log(this.sidoCode);
      this.CLEAR_GUGUN_LIST();
      this.gugunCode = null;
      if (this.sidoCode) this.getGugun(this.sidoCode);
    },
    dongList() {
      this.CLEAR_DONG_LIST();
      this.dongCode = null;
      if (this.gugunCode) this.getDong(this.gugunCode);
    },
    houseList() {
      if (this.gugunCode) this.getHouseList(this.gugunCode);
      if (this.gugunCode) this.getOfficetelList(this.gugunCode);
      this.$emit("changeToggle2", true);
    }
  }
};
