import { apiInstance } from "@/api/index.js";

const http = apiInstance();

const mapStore = {
  namespaced: true,
  state: {
    sidos: [{ value: null, text: "선택하세요" }],
    guguns: [{ value: null, text: "선택하세요" }],
    dongs: [{ value: null, text: "선택하세요" }],
    houses: [],
    officetels: [],
    house: {},
    selectsido: 0,
    likehouse: [],
  },
  getters: {},
  mutations: {
    SET_SIDO_LIST(state, sidos) {
      sidos.forEach((sido) => {
        // console.log("setsido", sido);
        state.sidos.push({ value: sido.sidoCode, text: sido.sidoName });
      });
    },
    SET_GUGUN_LIST(state, guguns) {
      guguns.forEach((gugun) => {
        state.guguns.push({ value: gugun.gugunCode, text: gugun.gugunName });
      });
    },
    SET_DONG_LIST(state, dongs) {
      dongs.forEach((dong) => {
        state.dongs.push({ value: dong.dongCode, text: dong.dongName });
      });
    },
    CLEAR_SIDO_LIST(state) {
      state.sidos = [{ value: null, text: "선택하세요" }];
    },
    CLEAR_GUGUN_LIST(state) {
      state.guguns = [{ value: null, text: "선택하세요" }];
    },
    CLEAR_DONG_LIST(state) {
      state.dongs = [{ value: null, text: "선택하세요" }];
    },
    CLEAR_DETAIL_HOUSE(state) {
      state.house = {}
    },
    SET_HOUSE_LIST: (state, houses) => {
      state.houses = houses;
    },
    SET_OFFICETEL_LIST: (state, officetels) => {
      state.officetels = officetels;
    },
    SET_DETAIL_HOUSE: (state, house) => {
      state.house = house;
    },
    SET_SELECT_SIDO: (state, selectsido) => {
      state.selectsido = selectsido;
    },
    SET_LIKE_HOUSE: (state, likehouse) => {
      state.likehouse = likehouse;
    },
    PUT_LIKE_HOUSE: (state, house) => {
      state.likehouse.push(house);
    }
  },
  actions: {
    getSido({ commit }) {
      http
        .get(`/house/sido`)
        .then(({ data }) => {
          // console.log(data);
          commit("SET_SIDO_LIST", data);
        })
        .catch((error) => {
          console.log(error);
        });
    },
    getGugun({ commit }, sidoCode) {
      http
        .get(`/house/gugun/${sidoCode}`)
        .then(({ data }) => {
          // console.log(commit, response);
          commit("SET_GUGUN_LIST", data);
          commit("SET_SELECT_SIDO", sidoCode);
        })
        .catch((error) => {
          console.log(error);
        });
    },
    getDong({ commit }, gugunCode) {
      http
        .get(`/house/dong/${gugunCode}`)
        .then(({ data }) => {
          // console.log(commit, response);
          commit("SET_DONG_LIST", data);
        })
        .catch((error) => {
          console.log(error);
        });
    },
    getHouseList: ({ commit }, gugunCode) => {
      const gCode = gugunCode.substr(0, 5);
      const ymd = "202305";

      const SERVICE_KEY =
        "U2FbirzZ%2BJstO00xy5XipInhYjiiitS439bouJlDMu9o%2BZEuxlNjdNS1D%2ByGOvAEAq4Em1GfiDTwjaoYj08pWg%3D%3D";

      const url =
        "http://openapi.molit.go.kr:8081/OpenAPI_ToolInstallPackage/service/rest/RTMSOBJSvc/getRTMSDataSvcRHRent?serviceKey=" +
        SERVICE_KEY +
        "&LAWD_CD=" +
        gCode +
        "&DEAL_YMD=" +
        ymd;

      http
        .get(url)
        .then(({ data }) => {
          // console.log(data);
          // console.log(data.response.body.items.item);
          commit("SET_HOUSE_LIST", data.response.body.items.item);
        })
        .catch((error) => {
          console.log(error);
        });
    },
    getOfficetelList: ({ commit }, gugunCode) => {
      const gCode = gugunCode.substr(0, 5);
      const ymd = "202305";

      const SERVICE_KEY =
        "U2FbirzZ%2BJstO00xy5XipInhYjiiitS439bouJlDMu9o%2BZEuxlNjdNS1D%2ByGOvAEAq4Em1GfiDTwjaoYj08pWg%3D%3D";

      const url =
        "http://openapi.molit.go.kr/OpenAPI_ToolInstallPackage/service/rest/RTMSOBJSvc/getRTMSDataSvcOffiRent?serviceKey=" +
        SERVICE_KEY +
        "&LAWD_CD=" +
        gCode +
        "&DEAL_YMD=" +
        ymd;

      http
        .get(url)
        .then(({ data }) => {
          // console.log(data);
          console.log(data.response.body.items.item);
          commit("SET_OFFICETEL_LIST", data.response.body.items.item);
        })
        .catch((error) => {
          console.log(error);
        });
    },
    setHouseDetail: ({ commit }, house) => {
      commit("SET_DETAIL_HOUSE", house);
    },
    putLikeHouse: ({ commit }, house) => {
      commit("PUT_LIKE_HOUSE", house);
    },
    deleteLikeHouse: ({ commit }, house) => {

      for (let i = 0; i < this.likehouse.length; i++) {
        const element = this.likehouse[i];

      }
    }
  },
};

export default mapStore;
