import { mapState } from "vuex";
const mapStore = "mapStore";
export default {
  name: "OfficeMap",
  components: {},
  data() {
    return {
      map: null,
      selectHouse: {}
    };
  },
  computed: {
    ...mapState(mapStore, ["house"])
  },
  created() {},
  mounted() {
    if (window.kakao && window.kakao.mpas) {
      this.loadMap(33.450701, 126.570667);
    } else {
      this.loadScript();
    }
  },
  methods: {
    // api 불러오기
    loadScript() {
      const script = document.createElement("script");
      script.src = "//dapi.kakao.com/v2/maps/sdk.js?appkey=2c45a57d65518ab4eebc76e4b1a15772&autoload=false";
      script.onload = () => window.kakao.maps.load(this.loadMap);
      document.head.appendChild(script);
    },
    // 맵 출력하기
    loadMap(lat, lng) {
      const container = document.getElementById("map");
      const options = {
        center: new window.kakao.maps.LatLng(lat, lng),
        level: 3
      };
      this.map = new window.kakao.maps.Map(container, options);
      this.loadMaker(lat, lng);
    },
    // 지정한 위치에 마커 불러오기
    loadMaker(lat, lng) {
      const markerPosition = new window.kakao.maps.LatLng(lat, lng);
      const marker = new window.kakao.maps.Marker({
        position: markerPosition
      });
      marker.setMap(this.map);
    },
    // 장소 좌표로 변경
    transLatLng() {
      let geocoder = new kakao.maps.services.Geocoder();
      geocoder.addressSearch("제주특별자치도 제주시 첨단로 242", function (result, status) {
        // 정상적으로 검색이 완료됐으면
        if (status === kakao.maps.services.Status.OK) {
          var coords = new kakao.maps.LatLng(result[0].y, result[0].x);

          // 결과값으로 받은 위치를 마커로 표시합니다
          var marker = new kakao.maps.Marker({
            map: this.map,
            position: coords
          });

          // 인포윈도우로 장소에 대한 설명을 표시합니다
          var infowindow = new kakao.maps.InfoWindow({
            content: '<div style="width:150px;text-align:center;padding:6px 0;">우리회사</div>'
          });
          infowindow.open(map, marker);

          // 지도의 중심을 결과값으로 받은 위치로 이동시킵니다
          map.setCenter(coords);
        }
      });
    }
  },
  watch: {
    house() {
      this.selectHouse = this.house;
    }
  }
};
