import { mapState, mapActions, mapMutations } from "vuex";
const mapStore = "mapStore";
export default {
  name: "HouseSearchBar",
  data() {
    return {
      sidoCode: null,
      gugunCode: null,
      dongCode: null
    };
  },
  computed: {
    ...mapState(mapStore, ["sidos", "guguns", "dongs", "houses"])
  },
  created() {
    this.CLEAR_SIDO_LIST();
    this.getSido();
  },
  methods: {
    ...mapActions(mapStore, ["getSido", "getGugun", "getDong", "getHouseList"]),
    ...mapMutations(mapStore, ["CLEAR_SIDO_LIST", "CLEAR_GUGUN_LIST", "CLEAR_DONG_LIST", "SET_HOUSE_LIST"]),
    gugunList() {
      this.CLEAR_GUGUN_LIST();
      this.gugunCode = null;
      if (this.sidoCode) this.getGugun(this.sidoCode);
    },
    dongList() {
      this.CLEAR_DONG_LIST();
      this.dongCode = null;
      if (this.gugunCode) this.getDong(this.gugunCode);
    },
    houseList() {
      const selectedDongs = this.dongs.filter(dong => dong.value === this.dongCode);
      const selectedDongTexts = selectedDongs.map(dong => dong.text);
      const dongText = selectedDongTexts.length > 0 ? selectedDongTexts[0] : null;
      const payload = {
        gugunCode: this.gugunCode.toString().substr(0, 5),
        dongText: dongText
      };
      if (this.gugunCode) {
        this.getHouseList(payload);
      }
      this.$emit("changeToggle2", true);
    },
    searchLib() {
      // var keyword = "도서관";
    }
  }
};
