import { mapState, mapActions, mapMutations } from "vuex";
const mapStore = "mapStore";
export default {
  name: "OfficeSearchBar",
  data() {
    return {
      sidoCode: null,
      gugunCode: null,
      dongCode: null
    };
  },
  computed: {
    ...mapState(mapStore, ["sidos", "guguns", "dongs", "officetels"])
  },
  created() {
    this.CLEAR_SIDO_LIST();
    this.getSido();
  },
  methods: {
    ...mapActions(mapStore, ["getSido", "getGugun", "getDong", "getOfficetelList"]),
    ...mapMutations(mapStore, ["CLEAR_SIDO_LIST", "CLEAR_GUGUN_LIST", "CLEAR_DONG_LIST", "SET_OFFICETEL_LIST"]),
    gugunList() {
      // console.log("sido:", this.sidoCode);
      // console.log("getSido", this.getSido);
      this.CLEAR_GUGUN_LIST();
      this.gugunCode = null;
      if (this.sidoCode) this.getGugun(this.sidoCode);
    },
    dongList() {
      this.CLEAR_DONG_LIST();
      this.dongCode = null;
      if (this.gugunCode) this.getDong(this.gugunCode);
    },
    officeList() {
      const selectedDongs = this.dongs.filter(dong => dong.value === this.dongCode);
      const selectedDongTexts = selectedDongs.map(dong => dong.text);
      if (selectedDongTexts.length > 0) {
        console.log("선택된 동 이름들:", selectedDongTexts);
      }
      if (this.gugunCode) {
        this.getOfficetelList(this.gugunCode);
      }
      console.log("선택된 동 이름:", this.dongCode);
      console.log("dongs? ", this.dongs);
      if (this.gugunCode) this.getOfficetelList(this.gugunCode);
      this.$emit("changeToggle2", true);
    }
  }
};
